                  if (autoScrolling) {
                      return;
                  }
      
                  var scrollY = window.scrollY + 5;
                  var windowHeight = window.innerHeight;
      
                  for (var i = 0; i < sections.length; i++) {
                      var section = sections[i];
                      var sectionTop = section.offsetTop;
                      var sectionHeight = section.offsetHeight;
      
